import React, { Component } from 'react';


const API = 'AIzaSyBRsK1vCzRHXrFKj2X_tYkhnQuIvR_qOVw'
const channelID = 'UCRAPKD2VxfVTvpd2esbCzfg'
const result = 10
// https://www.googleapis.com/youtube/v3/search?key=AIzaSyBRsK1vCzRHXrFKj2X_tYkhnQuIvR_qOVw&channelId=UCRAPKD2VxfVTvpd2esbCzfg&part=snippet,id&order=date&maxResults=10
var finalURL = `https://www.googleapis.com/youtube/v3/search?key=${API}&channelId=${channelID}&part=snippet,id&order=date&maxResults=${result}`

class Videos extends Component {
  constructor(props){
    super(props);

    this.state = {
      resultyt: []
    };
    this.clicked = this.clicked.bind(this);
  }
  clicked(){
    fetch(finalURL)
    .then((response) => response.json())
    .then((responseJson)=>{
      const resultyt = responseJson.items.map(obj => 'https://www.youtube.com/embed/'+obj.id.videoId);
      this.setState({resultyt});
    })
    .catch((error) => {
      console.error(error);
    });
  }
  render(){

  return (
    <React.Fragment >
    <button >Get youtube videos</button>
    // console.log(this.state.resultyt)
    <h1 onLoad={this.clicked}>Videos</h1>
    {
      this.state.resultyt.map((link, i) =>{
        console.log(link);
        var frame = <div key={i}><iframe  width="560" height="315" src={link} frameBorder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></iframe></div>
          return frame;
      })

    }
    {this.frame}


    </React.Fragment>
  )
}
}

export default Videos;
